    public static void main(String[] args) throws IOException {
        Scanner in = new Scanner(System.in);
        int N = in.nextInt();
        int K = in.nextInt();
        if (K > N / 2) {
            K = N - K;
        }
        int M = N - K;
        ArrayList<Integer> list = new ArrayList<>();
        for (int i = M + 1; i <= N; i++) {
            for (int n : prime[i]) {
                list.add(n);
            }
        }
        for (int i = 2; i <= K; i++) {
            for (Integer n : prime[i]) {
                list.remove(n);
            }
        }
        BigInteger res = BigInteger.valueOf(1);
        for (int n : list) {
            res = res.multiply(BigInteger.valueOf(n));
        }
        System.out.println(res.mod(BigInteger.valueOf(10007)));
    }

